mapper.select:
select * from t_match

    where to_days(startTime) < to_days(now()) ORDER BY hot LIMIT 0,4;


      WHERE YEARWEEK(date_format(startTime,'%Y-%m-%d')) = YEARWEEK(now()) ORDER BY hot LIMIT 0,4;


      WHERE DATE_FORMAT( startTime, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' ) ORDER BY hot LIMIT 0,4;
